PRAGMA defer_foreign_keys=TRUE;
CREATE TABLE d1_migrations(
		id         INTEGER PRIMARY KEY AUTOINCREMENT,
		name       TEXT UNIQUE,
		applied_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL
);
CREATE TABLE users (
  id TEXT PRIMARY KEY,
  email TEXT UNIQUE NOT NULL,
  password_hash TEXT NOT NULL,
  created_at INTEGER NOT NULL DEFAULT (unixepoch())
);
CREATE TABLE arca_accounts (
  id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  name TEXT NOT NULL,
  api_key_encrypted TEXT NOT NULL,
  api_token_encrypted TEXT, 
  cuit TEXT, 
  afip_username_encrypted TEXT, 
  afip_password_encrypted TEXT, 
  cert_encrypted TEXT, 
  key_encrypted TEXT, 
  provider TEXT NOT NULL DEFAULT 'afip_sdk', 
  created_at INTEGER NOT NULL DEFAULT (unixepoch()), is_default INTEGER DEFAULT 0,
  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE
);
CREATE TABLE invoices (
  id TEXT PRIMARY KEY,
  arca_account_id TEXT NOT NULL,
  arca_invoice_id TEXT NOT NULL,
  amount REAL NOT NULL,
  date INTEGER NOT NULL,
  description TEXT,
  cached_data TEXT, 
  created_at INTEGER NOT NULL DEFAULT (unixepoch()),
  updated_at INTEGER NOT NULL DEFAULT (unixepoch()),
  FOREIGN KEY (arca_account_id) REFERENCES arca_accounts(id) ON DELETE CASCADE
);
CREATE TABLE invoice_templates (
  id TEXT PRIMARY KEY,
  arca_account_id TEXT NOT NULL,
  description TEXT NOT NULL,
  default_amount REAL,
  created_at INTEGER NOT NULL DEFAULT (unixepoch()),
  FOREIGN KEY (arca_account_id) REFERENCES arca_accounts(id) ON DELETE CASCADE
);
CREATE TABLE billing_limits (
  arca_account_id TEXT PRIMARY KEY,
  category TEXT NOT NULL, 
  limit_amount REAL NOT NULL,
  alert_threshold REAL NOT NULL DEFAULT 0.8, next_due_amount REAL, next_due_date TEXT, billing_update_date TEXT, billed_amount REAL, 
  FOREIGN KEY (arca_account_id) REFERENCES arca_accounts(id) ON DELETE CASCADE
);
CREATE TABLE category_history (
  id TEXT PRIMARY KEY,
  arca_account_id TEXT NOT NULL,
  period TEXT NOT NULL,              -- Formato: "2026-01" (Enero 2026) o "2025-07" (Julio 2025)
  category TEXT NOT NULL,            -- A, B, C, D, E, F, G, H, I, J, K
  total_billed INTEGER,              -- Total facturado en ese perodo (opcional, para registro)
  notes TEXT,                        -- Notas opcionales
  created_at INTEGER DEFAULT (strftime('%s', 'now')),
  updated_at INTEGER DEFAULT (strftime('%s', 'now')),
  FOREIGN KEY (arca_account_id) REFERENCES arca_accounts(id) ON DELETE CASCADE,
  UNIQUE(arca_account_id, period)
);
CREATE TABLE monotributo_limits_history (
  id TEXT PRIMARY KEY,
  period TEXT NOT NULL UNIQUE,       -- Formato: "2026-01" o "2025-07"
  valid_from INTEGER NOT NULL,       -- Fecha desde la que aplican (timestamp)
  valid_until INTEGER,               -- Fecha hasta la que aplican (null = vigente)
  limits_json TEXT NOT NULL,         -- JSON con lmites por categora {"A": 8992597.87, "B": 13175201.52, ...}
  source TEXT,                       -- Fuente: "AFIP", "manual", etc.
  notes TEXT,                        -- Notas opcionales (ej: "Actualizacin por IPC")
  created_at INTEGER DEFAULT (strftime('%s', 'now'))
);
DELETE FROM sqlite_sequence;
CREATE INDEX idx_invoices_account_date ON invoices(arca_account_id, date DESC);
CREATE INDEX idx_invoices_date ON invoices(date DESC);
CREATE INDEX idx_arca_accounts_user ON arca_accounts(user_id);
CREATE INDEX idx_arca_accounts_user_default ON arca_accounts(user_id, is_default);
CREATE INDEX idx_category_history_account ON category_history(arca_account_id);
CREATE INDEX idx_category_history_period ON category_history(period);
CREATE INDEX idx_limits_history_valid_from ON monotributo_limits_history(valid_from);
